#Creating customer table
CREATE TABLE customer (cust_id INT,cust_name VARCHAR(50),date DATE CHECK (date BETWEEN '2023-01-01' AND '2023-06-30'),city VARCHAR(50),state VARCHAR(50),email_id VARCHAR(50),device VARCHAR(50),
channel VARCHAR(20),in_time TIMESTAMP,out_time TIMESTAMP,CONSTRAINT check_time_variation CHECK (out_time >= in_time + INTERVAL '1' HOUR AND out_time <= in_time + INTERVAL '2' HOUR),primary key (cust_id,date));

